body.script:
function rating(el, rating) {
document.querySelectorAll('#rating span').forEach((link) => {
link.style.color = 'black';
link.style.fontSize = '30px';
});
el.style.color = '#ff5722';
el.style.fontSize = '40px';
document.getElementById('rating_data').value = rating;
console.log(document.getElementById('rating_data').value);
}

