let player = document.getElementById("player");
let pop = document.getElementById("popup1");
let survey = document.getElementById("popup2");
let name = document.getElementById("name");
let email = document.getElementById("email");
let phone = document.getElementById("phone");
let time_to_add = document.getElementById("time_to_add");
fullscreenButton = document.getElementById('full-screen-btn');
const volumeBar = document.getElementById('volume-bar');
let flag = 0;
window.onload = function(){
play = document.getElementById("play");
play.addEventListener("click",function(){
if (name.value == '') {
alert('Enter Your Name');
return;
}
if (email.value == '') {
alert('Enter Your email');
return;
}
if (phone.value == '' || phone.value.length < 10 || phone.value.length > 10) {
alert('Enter a valid mobile Number');
return;
}
player.play();
pop.style.display = 'none';
startFakeChat(parseInt(time_to_add.innerHTML.trim()));
});
}
player.addEventListener('pause', function() {
// Immediately play the video when it's paused
if (flag == 0) {
player.play().catch(error => {
// Handle potential errors (e.g., autoplay blocked by the browser)
console.error("Autoplay prevented:", error);
});
}
});
player.addEventListener('ended', function() {
flag = 1;
survey.style.display = 'flex';
player.pause();
});

fullscreenButton.addEventListener('click', () => {
if (player.requestFullscreen) {
player.requestFullscreen();
} else if (player.mozRequestFullScreen) {
player.mozRequestFullScreen();
} else if (player.webkitRequestFullscreen) {
player.webkitRequestFullscreen();
} else if (player.msRequestFullscreen) {
player.msRequestFullscreen();
}
});
volumeBar.addEventListener('input', () => {
player.volume = volumeBar.value;
});

// Set the date we're counting down to
var date = document.getElementById('date');
console.log(date.innerHTML);
var countDownDate = new Date(date.innerHTML.trim()).getTime();

// Update the count down every 1 second
var x = setInterval(function() {
if (date.innerHTML.trim() == '') {
clearInterval(x);
return;
}
// Get today's date and time
var now = new Date().getTime();

// Find the distance between now and the count down date
var distance = countDownDate - now;

// Time calculations for days, hours, minutes and seconds
var days = Math.floor(distance / (1000 * 60 * 60 * 24));
var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
var seconds = Math.floor((distance % (1000 * 60)) / 1000);

// Display the result in the element with id="demo"
document.getElementById("demo").innerHTML = days + "d " + hours + "h "
+ minutes + "m " + seconds + "s ";

// If the count down is finished, write some text
if (distance < 0) {
clearInterval(x);
location.reload();
}
}, 1000);
