div.script:
document.querySelectorAll(".accordion-title1").forEach(title => {
title.addEventListener("click", function () {
const currentlyOpen = document.querySelector(".accordion-section1.expanded");
if (currentlyOpen && currentlyOpen !== this.parentElement) {
currentlyOpen.classList.remove("expanded");
}
this.parentElement.classList.toggle("expanded");
});
});

document.querySelectorAll(".accordion-header1").forEach(header => {
header.addEventListener("click", function () {
const currentActive = document.querySelector(".accordion-item1.active");
if (currentActive && currentActive !== this.parentElement) {
currentActive.classList.remove("active");
}
this.parentElement.classList.toggle("active");
});
});

template.script:
            // Set the date we're counting down to
            const cart1 = document.getElementById("cart1");
            const cart2 = document.getElementById("cart2");
            const cart3 = document.getElementById("cart3");
            const cart1_hidden = document.getElementById("cart1_hidden");
            const cart2_hidden = document.getElementById("cart2_hidden");
            const cart3_hidden = document.getElementById("cart3_hidden");
            const cart4 = document.getElementById("cart4");
            const cart4_hidden = document.getElementById("cart4_hidden");
            const expire_date = document.getElementById("expired");
            var countDownDate = new Date(expire_date.value).getTime();
            let open_pop = document.getElementById("open_pop");
            let close = document.getElementById("popup1");
            function popclose() {
            close.style.display= 'none';
            }
            function openPop() {
            close.style.display = 'flex';
            }
            if (expire_date.value == '') {
            document.getElementById("pop_title").innerHTML = 'Referral Link Not Generated';
            document.getElementById("pop_text").innerHTML = 'To Earn Commission, Generate the Referral Link
and share it with Customer 

 Generate Link Now';
            cart1.style.display = 'none';
            cart1_hidden.removeAttribute("hidden");
            cart2.style.display = 'none';
            cart2_hidden.removeAttribute("hidden");
            cart3.style.display = 'none';
            cart3_hidden.removeAttribute("hidden");
            cart4.style.display = 'none';
            cart4_hidden.removeAttribute("hidden");
            }
            if (countDownDate >= 2058843862000) {
            console.log(expire_date);
            document.getElementById("demo").classList.add('d-none');
            document.getElementById("demores").classList.add('d-none');
            }
            if (expire_date.value) {
            // Update the count down every 1 second
            var x = setInterval(function() {

            // Get today's date and time
            var now = new Date().getTime();

            // Find the distance between now and the count down date
            var distance = countDownDate - now;

            // Time calculations for days, hours, minutes and seconds
            var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
            var seconds = Math.floor((distance % (1000 * 60)) / 1000);

            // Display the result in the element with id="demo"
            document.getElementById("demo").innerHTML = minutes + "m " + seconds + "s " + "Left";
            document.getElementById("demores").innerHTML = minutes + "m " + seconds + "s " + "Left";

            // If the count down is finished, write some text
            if (distance < 30936){
            document.getElementById("demo").classList.add("text-danger");
            document.getElementById("demores").classList.add("text-danger");
            }
            if (distance < 0) {
            clearInterval(x);
            document.getElementById("demo").innerHTML = "LINK EXPIRED";
            document.getElementById("demores").innerHTML = "LINK EXPIRED";
            cart1.style.display = 'none';
            cart1_hidden.removeAttribute("hidden");
            cart2.style.display = 'none';
            cart2_hidden.removeAttribute("hidden");
            cart3.style.display = 'none';
            cart3_hidden.removeAttribute("hidden");
            cart4.style.display = 'none';
            cart4_hidden.removeAttribute("hidden");
            document.getElementById("demo").classList.remove('blink_me');
            document.getElementById("demores").classList.remove('blink_me');
            }
            }, 1000);
            }
