a.style:
display: inline-block; margin-top: 10px; padding:
                               5px 10px; background-color: #dc3545; color: #fff; text-decoration:
                               none; border-radius: 3px;

div.script:
document.querySelectorAll(".accordion-header2").forEach(header => {
header.addEventListener("click", function () {
const currentActive = document.querySelector(".accordion-item2.active");
if (currentActive && currentActive !== this.parentElement) {
currentActive.classList.remove("active");
}
this.parentElement.classList.toggle("active");
});
});

$('#carouselreview').on('touchstart', function(event){
const xClick = event.originalEvent.touches[0].pageX;
$(this).one('touchmove', function(event){
const xMove = event.originalEvent.touches[0].pageX;
const sensitivityInPx = 5;

if( Math.floor(xClick - xMove) > sensitivityInPx ){
$(this).carousel('next');
}
else if( Math.floor(xClick - xMove)
<
-sensitivityInPx ){
$(this).carousel('prev');
}
});
$(this).on('touchend', function(){
$(this).off('touchmove');
});
});

document.addEventListener("DOMContentLoaded", function () {
let sec_faqButtons1 = document.querySelectorAll(".sec_faq-button1");

sec_faqButtons1.forEach(button => {
button.addEventListener("click", function () {
let icon = this.querySelector(".sec_28_toggle-icon");
let isCollapsed = this.classList.contains("collapsed");

// Reset all icons to "plus"
sec_faqButtons1.forEach(btn => {
let btnIcon = btn.querySelector(".sec_28_toggle-icon");
btnIcon.src = "/kreator_website/static/src/img/plus.svg";
});

// Change icon based on state
if (isCollapsed) {
icon.src = "/kreator_website/static/src/img/minus.svg";
} else {
icon.src = "/kreator_website/static/src/img/plus.svg";
}
});
});

// Handle Bootstrap's accordion events for toggling icons
document.querySelectorAll('.accordion-collapse1').forEach(collapse => {
collapse.addEventListener('shown.bs.collapse', function () {
let button = this.previousElementSibling.querySelector('.sec_faq-button1');
let icon = button.querySelector(".sec_28_toggle-icon");
icon.src = "/kreator_website/static/src/img/minus.svg"; // Set to minus when opened
});

collapse.addEventListener('hidden.bs.collapse', function () {
let button = this.previousElementSibling.querySelector('.sec_faq-button1');
let icon = button.querySelector(".sec_28_toggle-icon");
icon.src = "/kreator_website/static/src/img/plus.svg"; // Set to plus when closed
});
});
});
