template.script:
document.querySelectorAll(".accordion-header").forEach(header => {
header.addEventListener("click", function () {
const currentActive = document.querySelector(".accordion-item.active");
if (currentActive && currentActive !== this.parentElement) {
currentActive.classList.remove("active");
}
this.parentElement.classList.toggle("active");
});
});

div.script:
document.querySelectorAll(".accordion-title").forEach(title => {
title.addEventListener("click", function () {
const currentlyOpen = document.querySelector(".accordion-section.expanded");
if (currentlyOpen && currentlyOpen !== this.parentElement) {
currentlyOpen.classList.remove("expanded");
}
this.parentElement.classList.toggle("expanded");
});
});

document.addEventListener("DOMContentLoaded", function () {
let sec_19_accordionButtons = document.querySelectorAll(".sec_19_accordion-button");

sec_19_accordionButtons.forEach(button => {
button.addEventListener("click", function () {
let icon = this.querySelector(".sec_19_toggle-icon");
let isCollapsed = this.classList.contains("collapsed");

// Reset all icons to "plus"
sec_19_accordionButtons.forEach(btn => {
let btnIcon = btn.querySelector(".sec_19_toggle-icon");
btnIcon.src = "/kreator_website/static/src/img/plus.svg";
});

// Change icon based on state
if (isCollapsed) {
icon.src = "/kreator_website/static/src/img/minus.svg";
} else {
icon.src = "/kreator_website/static/src/img/plus.svg";
}
});
});

// Handle Bootstrap's accordion events for toggling icons
document.querySelectorAll('.accordion-collapse').forEach(collapse => {
collapse.addEventListener('shown.bs.collapse', function () {
let button = this.previousElementSibling.querySelector('.sec_19_accordion-button');
let icon = button.querySelector(".sec_19_toggle-icon");
icon.src = "/kreator_website/static/src/img/minus.svg"; // Set to minus when opened
});

collapse.addEventListener('hidden.bs.collapse', function () {
let button = this.previousElementSibling.querySelector('.sec_19_accordion-button');
let icon = button.querySelector(".sec_19_toggle-icon");
icon.src = "/kreator_website/static/src/img/plus.svg"; // Set to plus when closed
});
});
});

