template.script:
const lead_pop = document.getElementById('popupLead');
const name = document.getElementById('name');
const email = document.getElementById('email');
const phno = document.getElementById('phno');
const info = document.getElementById('info');
const lead_title = document.getElementById('lead_title');
const sub_title = document.getElementById('sub_title');
const lead_form = document.getElementById('lead_form');
const lead_video = document.getElementById('lead_video');
const course_name = document.getElementById('course_name');

function openPreview(landing, video) {
fetch('/preview/video?landing=' + landing +'&video=' + video, {
                method: 'POST',
                })
                .then(response => response.json())
                .then(data => {
                        if (data['response'] == 'success') {
                            if (data['submitted'] == 'no') {
                            info.innerHTML = 'Please Enter your details to view the video';
                            lead_title.innerHTML = 'Preview';
                            sub_title.innerHTML = '';
                            lead_form.style.display = 'block';
                            lead_video.innerHTML = data['video'];
                            lead_video.style.display = 'none';
                            lead_form.style.width = '50vw';
                            lead_form.style.height = '50vh';
                            lead_form.style.paddingTop = '80px';
                            lead_form.style.border = '1px solid black';
                            lead_form.style.backgroundImage = "url('/kreator_website/static/src/img/blur_video.png')";
                            }
                            if (data['submitted'] == 'yes') {
                            info.innerHTML = '';
                            lead_title.innerHTML = 'Preview';
                            sub_title.innerHTML = '';
                            lead_form.style.display = 'none';
                            lead_video.style.display = 'block';
                            lead_video.innerHTML = data['video'];
                            }
                            openLead()
                            } else {
                                alert('Failed to Capture response');
                            }
                            })
                .catch(error => console.error('Fetch Error:', error));
}

let count = 10;
const timer = setInterval(function() {
count--;
if (count === 0) {
clearInterval(timer);
openLead('yes', 'no')
}
}, 1000);

function openLead(timer, video) {
openLead()
}
var re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
function submitLead() {
if (name.value == '') {
info.innerHTML = '<p class="text-danger" >Name should not Be Empty!</p>';
return;
}
else if (email.value == '' || re.test(email.value) == false) {
info.innerHTML = '<p class="text-danger" >Wrong or Empty Empty!</p>';
return;
}
else if (phno.value == '' || phno.value.length != 10) {
info.innerHTML = '<p class="text-danger" >Please Enter 10 Digit Phone Number</p>';
return;
}
else {
fetch('/lead/submission?name=' + name.value +'&email=' + email.value + '&phno=' + phno.value + '&course_name=' + course_name.value, {
                method: 'POST',
                })
                .then(response => response.json())
                .then(data => {
                if (data['response']) {
                    console.log(lead_video.innerHTML)
                    if (lead_video.innerHTML == '') {
                            info.innerHTML = '<p class="text-success" >'+ data['message'] +'</p>';
                            lead_title.innerHTML = 'Thank You for Showing Your Interest!';
                            sub_title.innerHTML = '';
                            lead_form.style.display = 'none';
                    }
                    if (lead_video.innerHTML != '') {
                            lead_form.style.display = 'none';
                            lead_video.style.display = 'block';
                    }
                } else {
                alert('Failed to Capture response');
                }
                })
                .catch(error => console.error('Fetch Error:', error));
                }
}

function closeLead() {
lead_pop.style.display= 'none';
lead_vid = lead_video.innerHTML;
lead_video.innerHTML = lead_vid;
}
function openLead() {
lead_pop.style.display = 'flex';
}

document.querySelectorAll(".accordion-header").forEach(header => {
header.addEventListener("click", function () {
const currentActive = document.querySelector(".accordion-item.active");
if (currentActive && currentActive !== this.parentElement) {
currentActive.classList.remove("active");
}
this.parentElement.classList.toggle("active");
});
});

document.querySelectorAll(".accordion-title").forEach(title => {
title.addEventListener("click", function () {
const parent = this.parentElement;
const body = parent.querySelector(".accordion-body");
const arrow = this.querySelector(".accordion-arrow");

if (parent.classList.contains("expanded")) {
body.style.maxHeight = "0";
arrow.style.transform = "rotate(0deg)";
} else {
document.querySelectorAll(".accordion-body").forEach(b => b.style.maxHeight = "0");
document.querySelectorAll(".accordion-arrow").forEach(a => a.style.transform = "rotate(0deg)");

body.style.maxHeight = body.scrollHeight + "px";
arrow.style.transform = "rotate(180deg)";
}

document.querySelectorAll(".accordion-section").forEach(section => section.classList.remove("expanded"));
parent.classList.toggle("expanded");
});

title.addEventListener("keydown", function (e) {
if (e.key === "Enter" || e.key === " ") {
this.click();
}
});
});

