function toggleFAQ(selected) {
document.querySelectorAll(".faq-card").forEach(card => {
let icon = card.querySelector(".icon");
let body = card.querySelector(".faq-body");

if (card === selected) {
let isActive = card.classList.contains("active");
card.classList.toggle("active");
if (isActive) {
icon.innerHTML = "−"; // Toggle icon
body.style.display = "block"; // Show answer
} else {
icon.innerHTML = "+"; // Toggle icon
body.style.display = "none"; // Hide answer
}
} else {
card.classList.remove("active");
card.querySelector(".icon").innerHTML = "+"; // Reset icon
card.querySelector(".faq-body").style.display = "none"; // Hide other answers
}
});
}
